
class Point {
  constructor (x, y) {
    this.x = x;
    this.y = y;
  }
  get length () {
    return Math.sqrt(this.x * this.x + this.y * this.y);
  }
  log () {
    setTimeout(() => {
      console.log(`x=${this.x} y=${this.y} length=${this.length}`);
    }, 100);
  }
}

let point = new Point(3, 4);
console.log(point.length);

function newPoint (x, y) {
  return {
    get x () { return x; },
    get y () { return y; },
    get length () { return Math.sqrt(x * x + y * y); }
  };
}

let point2 = newPoint(3, 4);
console.log(point2.length);
